mapper.select:
{ call #{cursor, mode=OUT, jdbcType=CURSOR,
         javaType=java.sql.ResultSet, resultMap=DailyPlanMap} :=
dailyplan_pkg.select_dailyplan_all() }

{ call #{cursor, mode=OUT, jdbcType=CURSOR,
         javaType=java.sql.ResultSet, resultMap=DailyPlanMap} :=
dailyplan_pkg.select_dailyplan_by_id(
#{id, jdbcType=NUMERIC}
)
}

{ call #{cursor, mode=OUT, jdbcType=CURSOR,
         javaType=java.sql.ResultSet, resultMap=DailyPlanMap} :=
